import React from "react";
import { useParams } from "react-router-dom";
import logo from "../logo.svg";
import Project from "../Data/Project";

const ProjectDetail = () => {
  const { slug } = useParams();
  const projects = Project.find((e) => e.slug === String(slug));
  return (
    <div className="h-screen flex items-center justify-center">
      <div className="container mx-auto">
        <div className="flex flex-row border bg-brown/25 backdrop-blur-sm">
          <div className="w-full">{logo}</div>
          <div className="w-full">
            <h1 className="text-4xl font-bold my-4">{projects.title}</h1>
            <p className="font-light text-base">{projects.description}</p>
            <div className="flex flex-row my-3">
              {projects.tech.map((data) => (
                <img src={data.item} className="w-18 p-2" alt="" />
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProjectDetail;
