import React from "react";
import { useParams } from "react-router-dom";
import logo from "../logo.svg";
import Project from "../Data/Project";
import { Link } from "react-router-dom";
import ProjectSlider from "../Components/ProjectSlider";
import DetailProject from "../Components/DetailProject";

const ProjectDetail = () => {
  const { slug } = useParams();
  const projects = Project.find((e) => e.slug === String(slug));
  return (
    <div className="h-auto flex items-center justify-center">
      <div className="container mx-auto">
        <Link to="/">
          <button className="mt-12 mx-8 text-white px-2 py-2 bg-brown">
            Back To Home
          </button>
        </Link>
        <div className="flex flex-row gap-5 border mt-3 mb-16 mx-8 bg-brown/25 backdrop-blur-sm">
          <ProjectSlider projects={projects} />
          <DetailProject projects={projects} />
        </div>
      </div>
    </div>
  );
};

export default ProjectDetail;
